import axios from 'axios';
const API_KEY = '43f7b7cce2ae5cabfc5e2f76a4248ec7';
import React, {useEffect, useState} from 'react';
const SearchBar = ({list, setList, setRequest}) => {
    var query = "";
    var request = "";
    const [order, setOrder] = useState("");
    const searchQuery = event => {
        if (event.target.value) {
            query = event.target.value;
            request = "https://api.themoviedb.org/3/search/movie?api_key=" + API_KEY + "&query=" + query + "&page=1";
            console.log(request);
            setRequest(request);
            axios.get(request)
            .then ((response) => {
                console.log(response);
                setList(list => []);
                setList(list => [...list, response.data.results]);
                setList(list => list.sort());
            })
            .catch(function(error) {
                console.log(error);
            });
        } else {
            setList(list => []);
        }
    }
    function chooseOrder(selected) {
        setOrder(selected);
    }
    return (
        <div id="search">
            <input type="text" placeholder="Search Movies" name="searchBar" id="searchBar" onChange={searchQuery}></input>
            <div id="sortOptions">
                <h5>Sort By: </h5>
                <input type="radio" id="title"  value="title"></input>
                <label for="title">Title  </label>
                <input type="radio" id="popularity" value="popularity"></input>
                <label for="popularity">Popularity</label>
            </div>
        </div>
    )
}
export default SearchBar
