import axios from 'axios';
import { useEffect , useState} from 'react';
const API_KEY = '43f7b7cce2ae5cabfc5e2f76a4248ec7';
import HeaderItem from './HeaderItem';
//import './GalleryHeader.css';
const GalleryHeader = ({genreList, setGenreList}) => {
    var request = "";
    const [isLoading, setLoading] = useState(true);
    useEffect(() => {
        if (genreList.length == 0) {
            request = "https://api.themoviedb.org/3/genre/movie/list?api_key=" + API_KEY + "&language=en-US";
            axios.get(request)
            .then ((response) => {
                console.log(response);
                setGenreList(genreList => [...genreList, response.data.genres]);
                setLoading(false);
            })
            .catch(function(error) {
                console.log(error);
            })
        }
    }, []);

    if (!isLoading) {
        console.log(genreList[0]);
        const content = genreList[0].map(genre => <HeaderItem key = {genre.id} genre = {genre}/>);
        console.log(content);

        return (
            <div id="genres">
                {content}
            </div>
        );
    }

} 
export default GalleryHeader
