import {useLocation} from 'react-router-dom'


const DetailCard = () =>{
    const location = useLocation();
    const{list, index} = location.state
    console.log(list);
    console.log(index);
    var movie = list[0][index];
    var poster = "https://image.tmdb.org/t/p/original" + movie.poster_path;
    var nextIndex = index + 1;
    if (index == 20) {
        nextIndex = 0;
    }
    var nextMovie = list[0][nextIndex];
    return (
        <div class="full-card">
            <div class="card">
                <div class="card-photo">
                    <img src={poster} id="moviePoster"></img>
                </div>
                <div class="card-text">
                    <h2>{movie.title}</h2>
                    <h3>Popularity: {movie.popularity}</h3>
                    <h4>{movie.release_date}</h4>
                </div>
            </div>
            <Link to={`/movie/${nextMovie.id}`} state={{list: props, index: nextIndex}}><h2>Next</h2></Link>
        </div>

    )
} 
export default DetailCard
