import axios from 'axios';
import { useEffect , useState} from 'react';
const API_KEY = '43f7b7cce2ae5cabfc5e2f76a4248ec7';
import HeaderItem from './HeaderItem';
import GalleryMovie from './GalleryMovie';
import './GalleryHeader.css';
const GalleryHeader = ({genreList, setGenreList, contentList, setContent}) => {
    var genre_request = "";
    var content_request = "";
    const [isGenreLoading, setGenreLoading] = useState(true);
    const [isContentLoading, setContentLoading] = useState(true);
    useEffect(() => {
        if (genreList.length == 0) {
            genre_request = "https://api.themoviedb.org/3/genre/movie/list?api_key=" + API_KEY + "&language=en-US";
            axios.get(genre_request)
            .then ((response) => {
                console.log(response);
                setGenreList(genreList => [...genreList, response.data.genres]);
                setGenreLoading(false);
            })
            .catch(function(error) {
                console.log(error);
            })
        }

        content_request = "https://api.themoviedb.org/3/trending/movie/week?api_key=" + API_KEY;
        axios.get(content_request)
            .then ((response) => {
                console.log(response);
                setContent(t => [...contentList, response.data.results]);
                setContentLoading(false);
            })
            .catch(function(error) {
                console.log(error);
            })
    }, []);

    function filterContentList(genre) {
        setContentLoading(true);
        content_request = "https://api.themoviedb.org/3/discover/movie?api_key=" + API_KEY + "&language=en-US&page=1&with_genres=" + genre
        axios.get(content_request)
            .then ((response) => {
                console.log(response);
                setContent(t => [...contentList, response.data.results]);
                setContentLoading(false);
                return;
            })
            .catch(function(error) {
                console.log(error);
        })
    }
    if (!isGenreLoading && !isContentLoading) {
        const genre_content = genreList[0].map(genre => <HeaderItem key = {genre.id} genre = {genre} onClick={filterContentList(genre.name)}/>);
        const content = contentList[0].map(movie => <GalleryMovie key={movie.id} movie = {movie}/>);
        console.log(genre_content);
        console.log(content);
        return (
            <div id="gallery_header">
                <div id="genres">
                    {genre_content}
                </div>
                <div id="trending">
                    {content}
                </div>
            </div>

        );
    }

} 
export default GalleryHeader
