import logo from './logo.svg';
import './App.css';
import { render } from '@testing-library/react';
import React, {useEffect, useState} from 'react';
import {searchQuery} from 'api'

function App() {
  //const axios = require('axios').default;
  const [query, setQuery] = useState("");
  const [posts, setList] = useState([]);

  const handleQuery = event => {
    setQuery(event.target.value);
    searchQuery();
  }

  useEffect(() => {
    searchQuery.then(json => {
      setList
    })
  })
  return (
    <div className="App">
      <h1>MovieFind</h1>
      <h2>Find the perfect movie for you!</h2>
      <div id="search">
        <input type="text" placeholder="Search" name="searchBar" id="searchBar" onChange={handleQuery}></input>
        <button id="searchButton" onClick={searchQuery}>Search</button>
      </div>

    </div>
  );

}

export default App;
