import axios from 'axios';
const API_KEY = '43f7b7cce2ae5cabfc5e2f76a4248ec7';
const SearchBar = ({list, setList, query, setQuery}) => {
    const handleQuery = event => {
        setQuery(event.target.value);
        searchQuery();
    }
    function searchQuery() {
        console.log(event.target.value);
        if (event.target.value) {
            console.log(query);
            //console.log("https://api.themoviedb.org/3/search/movie?api_key=" + API_KEY + "&query=" + query + "&page=1");
            axios.get("https://api.themoviedb.org/3/search/movie?api_key=" + API_KEY + "&query=" + query + "&page=1")
            .then ((response) => {
                //console.log(response);
                setList(list => []);
                setList(list => [...list, response.data.results]);
            })
            .catch(function(error) {
                console.log(error);
            });
        }
    }

    console.log(list);

    return (
        <div id="search">
            <input type="text" placeholder="Search Movies" name="searchBar" id="searchBar" onChange={handleQuery}></input>
            <button id="searchButton" onClick={handleQuery}>Search</button>
        </div>
    )
}
export default SearchBar
