import axios from 'axios';
const API_KEY = '43f7b7cce2ae5cabfc5e2f76a4248ec7';
const SearchBar = ({list, setList, query, setQuery}) => {
    const searchQuery = event => {
        setQuery(event.target.value);
        console.log("https://api.themoviedb.org/3/search/movie?api_key=" + API_KEY + "&query=" + query + "&page=1");
        axios.get("https://api.themoviedb.org/3/search/movie?api_key=" + API_KEY + "&query=" + query + "&page=1")
        .then ((response) => {
            console.log(response);
            setList(list => [...list, response]);
        });
        console.log(list);
    }

    return (
        <div id="search">
            <input type="text" placeholder="Search Movies" name="searchBar" id="searchBar" onChange={searchQuery}></input>
            <button id="searchButton" onClick={searchQuery}>Search</button>
        </div>
    )
}
export default SearchBar
