import axios from 'axios';
const API_KEY = '43f7b7cce2ae5cabfc5e2f76a4248ec7';
import React, {useEffect, useState} from 'react';
const SearchBar = ({list, setList, request, setRequest}) => {
    var query = "";
    const searchQuery = event => {
        if (event.target.value) {
            query = event.target.value;
            console.log(query);
            request = "https://api.themoviedb.org/3/search/movie?api_key=" + API_KEY + "&query=" + query + "&page=1";
            axios.get(request)
            .then ((response) => {
                console.log(response);
                setList(list => []);
                setList(list => [...list, response.data.results]);
            })
            .catch(function(error) {
                console.log(error);
            });
        } else {
            setList(list => []);
        }
    }

    return (
        <div id="search">
            <input type="text" placeholder="Search Movies" name="searchBar" id="searchBar" onChange={searchQuery}></input>
            <button id="searchButton" onClick={searchQuery}>Search</button>
        </div>
    )
}
export default SearchBar
